import useElementSize from "./useElementSize";

const CustomHook = () => {
  const size = useWindowSize();
  const componentRef = useRef();
  const [hide, setHide] = useState(true);
  const { width, height } = useElementSize(componentRef, hide);
  console.log(componentRef);
  return (
