const initialState = 0;

const countReducer = (countState, payload) => {
  console.log(countState, payload);
  switch (payload.type) {
    case "INCREMENT":
      return countState + 1;
      // break;
    case "DECREMENT":
      console.log(0 >= countState);
      if(0 >= countState) {
        return 0
      } else {
        return countState - 1;
      }
      // break;
    default :
    return countState;
  }
};
