import CreateNote from './CreateNote';
import ListNote from './ListNote';

const GoogleKeepApp = () => {

  const [addItem, setAddItem] = useState([]);

  const addNote = (note) => {
    setAddItem((prevData) => {
      return [...prevData, note];
    });
    console.log(note);
  }

  const onDelete = (id) => {
    setAddItem((oldDataNwe) => oldDataNwe.filter ((curItem, index) => {
      return index !== id;
    })

    );
