import { useState } from "react";
import { GoPlus } from "react-icons/go";

const CreateNote = ({ passNote }) => {
  const [show, setShow] = useState(false);
  const [note, setNote] = useState({
    title: "",
    content: "",
  });

  const inputEvent = (event) => {
    // const value = event.target.value;
